mapper.select:
select *
from ${tablename}


		and ID = #{id}


		and name = #{name}


		and type = #{type}


		and specification = #{specification}

mapper.insert:
insert into ${tablename} (ID, name, type, 
  specification, picture, retailPrice, 
  wholesalePrice, purchasePrice, time
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{specification,jdbcType=VARCHAR}, #{picture,jdbcType=VARCHAR}, #{retailprice,jdbcType=REAL}, 
  #{wholesaleprice,jdbcType=REAL}, #{purchaseprice,jdbcType=REAL}, #{time,jdbcType=VARCHAR}
  )

mapper.update:
update ${tablename}
set name = #{name,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR},
  specification = #{specification,jdbcType=VARCHAR},
  picture = #{picture,jdbcType=VARCHAR},
  retailPrice = #{retailprice,jdbcType=REAL},
  wholesalePrice = #{wholesaleprice,jdbcType=REAL},
  purchasePrice = #{purchaseprice,jdbcType=REAL},
  time = #{time,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
