mapper.select:
select *
from sub_branch_detail_map


		and ID = #{id}


		and warehourseID = #{warehourseid}


		and principalID = #{principalid}


		and label = #{label}

mapper.delete:
    delete from sub_branch_detail_map


			and ID = #{id}


			and warehourseID = #{warehourseid}


			and principalID = #{principalid}


			and label = #{label}
